<?php require_once(SBINTERFACES);require_once(SBCOMLOADER);// Concrete implementation for the generic service proxyclass ServiceProxy implements Service {	protected		// service name		$service,		// root		$root;		// Constructor	public function __construct($service, $root){		$this->service = $service;		$this->root = $root;	}		// Service interface	public function getOperation($name){		$cl = new ComponentLoader();		return $cl->load($this->service.'.'.$name, $root);	}}?>